import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading'); // loading, prank, final, success
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [yesStyle, setYesStyle] = useState({});

  // 1. Fake Loading Screen Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 1000);
            return 99;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. The Runaway "Yes" Button Logic
  const moveButton = () => {
    if (phase === 'prank') {
      const x = Math.random() * (window.innerWidth - 100);
      const y = Math.random() * (window.innerHeight - 50);
      setYesStyle({ position: 'fixed', left: `${x}px`, top: `${y}px`, transition: '0.1s' });
    }
  };

  // 3. The Kiss Explosion
  const triggerKisses = () => {
    setPhase('success');
    const duration = 5 * 1000;
    const animationEnd = Date.now() + duration;

    const frame = () => {
      confetti({
        particleCount: 3,
        angle: 60,
        spread: 55,
        origin: { x: 0 },
        shapes: ['circle'], // You can use custom SVGs for lips here
        colors: ['#ff0000', '#ff69b4', '#ff1493']
      });
      confetti({
        particleCount: 3,
        angle: 120,
        spread: 55,
        origin: { x: 1 },
        colors: ['#ff0000', '#ff69b4', '#ff1493']
      });

      if (Date.now() < animationEnd) {
        requestAnimationFrame(frame);
      }
    };
    frame();
  };

  return (
    <div style={styles.container}>
      {/* PHASE 1: LOADING */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <h2>System Update: Valentine.exe</h2>
          <div style={styles.progressBarOuter}>
            <div style={{ ...styles.progressBarInner, width: `${progress}%` }} />
          </div>
          <p>Processing late apology... {progress}%</p>
        </div>
      )}

      {/* PHASE 2: THE PRANK */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <img src="https://media.giphy.com/media/v1.Y2lkPTc5MGI3NjExOHpueGZ3bmZqZzRycmsyc3R5Z3R5Z3R5Z3R5Z3R5Z3R5Z3R5JmVwPXYxX2ludGVybmFsX2dpZl9ieV9pZCZjdD1n/OPU6wUKARA3NTLDAtE/giphy.gif" alt="sad" style={{width: '150px'}} />
          <h2>I missed the day... üçã</h2>
          <p>Since I'm late, do I still get to be your Valentine?</p>
          <div style={{ marginTop: '20px' }}>
            <button 
              onMouseEnter={moveButton} 
              onClick={moveButton} 
              style={{ ...styles.btnYes, ...yesStyle }}
            >
              YES!
            </button>
            <button 
              onClick={() => {
                setNoCount(noCount + 1);
                if(noCount > 3) setPhase('final');
              }} 
              style={styles.btnNo}
            >
              {noCount === 0 ? "No, you're a lemon." : "Still No."}
            </button>
          </div>
          {noCount > 0 && <p style={{color: 'red', fontSize: '12px'}}>Error: 'No' is not a valid option. Try harder.</p>}
        </div>
      )}

      {/* PHASE 3: THE REAL QUESTION */}
      {phase === 'final' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '3rem'}}>üíñ</h1>
          <h2>Okay, stop being angry...</h2>
          <p>I know I'm late, but you're the only one for me. <br/> <strong>Will you be my Belated Valentine?</strong></p>
          <button onClick={triggerKisses} style={styles.btnFinal}>YES, I FORGIVE YOU!</button>
        </div>
      )}

      {/* PHASE 4: SUCCESS */}
      {phase === 'success' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üíãüíãüíã</h1>
          <h2 style={{color: '#d63384'}}>MWAH!</h2>
          <p>I love you! Get ready for your real surprise!</p>
          <button onClick={() => setPhase('prank')} style={styles.btnReset}>Annoy me again</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff0f3',
    fontFamily: 'sans-serif',
    textAlign: 'center',
    overflow: 'hidden'
  },
  card: {
    padding: '40px',
    borderRadius: '20px',
    backgroundColor: 'white',
    boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
    maxWidth: '400px'
  },
  progressBarOuter: {
    width: '100%',
    height: '20px',
    backgroundColor: '#eee',
    borderRadius: '10px',
    marginTop: '20px'
  },
  progressBarInner: {
    height: '100%',
    backgroundColor: '#ff4d6d',
    borderRadius: '10px',
    transition: 'width 0.1s'
  },
  btnYes: {
    padding: '10px 25px',
    backgroundColor: '#ff4d6d',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginRight: '10px',
    fontWeight: 'bold'
  },
  btnNo: {
    padding: '10px 25px',
    backgroundColor: '#6c757d',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  btnFinal: {
    padding: '15px 30px',
    backgroundColor: '#d63384',
    color: 'white',
    fontSize: '1.2rem',
    border: 'none',
    borderRadius: '50px',
    cursor: 'pointer',
    boxShadow: '0 5px 15px rgba(214, 51, 132, 0.4)'
  },
  btnReset: {
    marginTop: '20px',
    background: 'none',
    border: 'none',
    color: '#aaa',
    cursor: 'pointer',
    textDecoration: 'underline'
  }
};

export default App;
