import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [messageIndex, setMessageIndex] = useState(0);

  const phrases = [
    "No", "Are you sure?", "Really sure??", "Think again!", "Last chance!",
    "Surely not?", "You're breaking my heart! üíî", "I'll cry...", "I'm crying now üò≠",
    "Okay, that's enough.", "PLEASEEE", "I'll give you chocolate! üç´", "Look behind you! (jk)",
    "Just click Yes already!", "You're mean! ü•∫", "I give up...", "Error 404: No not found"
  ];

  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 800);
            return 100;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth - 150);
    const y = Math.random() * (window.innerHeight - 150);

    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      transition: '0.15s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
      zIndex: 999,
      fontSize: `${Math.max(16 - noCount, 4)}px` // Button gets smaller!
    });

    setNoCount(noCount + 1);
    setMessageIndex(noCount % phrases.length);
    setYesScale(prev => prev + 0.2); // Yes button gets HUGE
  };

  const triggerSuccess = () => {
    setPhase('success');
    const duration = 10 * 1000;
    const animationEnd = Date.now() + duration;
    const defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

    const randomInRange = (min, max) => Math.random() * (max - min) + min;

    const interval = setInterval(function() {
      const timeLeft = animationEnd - Date.now();
      if (timeLeft <= 0) return clearInterval(interval);

      const particleCount = 50 * (timeLeft / duration);
      // Heart and Flower shapes for the "Success"
      confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.1, 0.3), y: Math.random() - 0.2 } });
      confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.7, 0.9), y: Math.random() - 0.2 } });
    }, 250);
  };

  return (
    <div style={styles.container}>
      {phase === 'loading' && (
        <div style={styles.card}>
          <div className="spinner" style={styles.spinner}>üíñ</div>
          <h2 style={styles.title}>System Apology Update</h2>
          <div style={styles.progressBarOuter}>
            <div style={{ ...styles.progressBarInner, width: `${progress}%` }} />
          </div>
          <p style={styles.text}>Scanning for forgiveness levels... {progress}%</p>
        </div>
      )}

      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '60px', animation: 'float 3s infinite ease-in-out'}}>üíù</div>
          <h2 style={styles.title}>I know I'm a bit late...</h2>
          <p style={styles.text}>
            But my heart is still running on February 14th time. <br/>
            <strong>Will you forgive me and be my Valentine?</strong>
          </p>

          <div style={styles.buttonGroup}>
            <button 
              onClick={triggerSuccess} 
              style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}
            >
              YES! üåπ
            </button>

            <button 
              onMouseEnter={moveNoButton}
              onClick={moveNoButton}
              style={{ ...styles.btnNo, ...noStyle }}
            >
              {phrases[messageIndex]}
            </button>
          </div>
        </div>
      )}

      {phase === 'success' && (
        <div style={{...styles.card, animation: 'popIn 0.5s ease-out'}}>
          <h1 style={{fontSize: '5rem', margin: '0'}}>üè†</h1>
          <h2 style={styles.title}>VICTORY! ‚ù§Ô∏è</h2>
          <p style={styles.text}>
            Contract Signed! You are officially stuck with me.<br/>
            <strong>Gift Delivery Inbound to Bennigana Halli!</strong>
          </p>
          <div style={styles.contract}>
            <p><strong>Terms & Conditions:</strong></p>
            <ul style={{textAlign: 'left', fontSize: '0.8rem'}}>
              <li>Unlimited hugs on demand.</li>
              <li>One "Get out of jail free" card for the user.</li>
              <li>Extra dessert for the Valentine.</li>
            </ul>
          </div>
          <p style={{marginTop: '20px', fontWeight: 'bold', color: '#ff4d6d'}}>I Love You! üòò</p>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    width: '100vw',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ffe5ec',
    fontFamily: '"Comic Sans MS", cursive, sans-serif',
    textAlign: 'center',
    overflow: 'hidden'
  },
  card: {
    padding: '40px',
    borderRadius: '30px',
    backgroundColor: 'white',
    boxShadow: '0 20px 50px rgba(255, 77, 109, 0.3)',
    maxWidth: '450px',
    width: '90%',
    position: 'relative'
  },
  title: { color: '#ff4d6d', fontSize: '1.8rem' },
  text: { color: '#555', lineHeight: '1.5' },
  progressBarOuter: { height: '12px', backgroundColor: '#ffd1dc', borderRadius: '10px', margin: '20px 0' },
  progressBarInner: { height: '100%', backgroundColor: '#ff4d6d', borderRadius: '10px', transition: 'width 0.1s linear' },
  buttonGroup: { display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', minHeight: '150px' },
  btnYes: {
    padding: '15px 35px',
    backgroundColor: '#ff4d6d',
    color: 'white',
    border: 'none',
    borderRadius: '50px',
    cursor: 'pointer',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    boxShadow: '0 4px 15px rgba(255, 77, 109, 0.4)'
  },
  btnNo: {
    padding: '10px 20px',
    backgroundColor: '#adb5bd',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  contract: {
    backgroundColor: '#fff0f3',
    padding: '15px',
    borderRadius: '15px',
    border: '1px dashed #ff4d6d',
    marginTop: '20px'
  },
  spinner: {
    fontSize: '40px',
    display: 'inline-block',
    animation: 'spin 2s linear infinite'
  }
};

export default App;
