import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => {
              setPhase('prank');
              setSidebarOpen(true);
            }, 800);
            return 99;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth * 0.6);
    const y = Math.random() * (window.innerHeight * 0.6);
    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      transition: '0.2s ease-out' 
    });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.2); // Yes button grows aggressively
  };

  const triggerKisses = () => {
    setPhase('success');
    const end = Date.now() + (5 * 1000);
    const frame = () => {
      confetti({
        particleCount: 5,
        angle: 60,
        spread: 55,
        origin: { x: 0, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      confetti({
        particleCount: 5,
        angle: 120,
        spread: 55,
        origin: { x: 1, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      if (Date.now() < end) requestAnimationFrame(frame);
    };
    frame();
  };

  return (
    <div className="relative h-screen w-screen bg-rose-50 flex items-center justify-center font-sans overflow-hidden">

      {/* SIDEBAR POP-IN */}
      <div className={`fixed top-0 h-full w-64 bg-rose-500 shadow-2xl transition-all duration-700 ease-in-out z-50 p-8 flex flex-col justify-center ${sidebarOpen ? 'left-0' : '-left-64'}`}>
        <h3 className="text-white text-xl font-bold mb-4">Why I'm Late:</h3>
        <ul className="text-rose-100 space-y-4 text-sm italic">
          <li>üçã Got stuck in a lemon tree</li>
          <li>üêâ Fought a dragon for your gift</li>
          <li>‚è∞ My clock was in a different timezone</li>
        </ul>
        <div className="mt-10 p-4 bg-rose-400/30 rounded-lg text-white text-xs">
          <strong>Forgiveness Level:</strong> {noCount > 5 ? "88% (Almost there!)" : "12% (Critical)"}
        </div>
      </div>

      {/* TICKER BOTTOM */}
      <div className="fixed bottom-0 w-full bg-rose-200 py-2 overflow-hidden whitespace-nowrap z-0">
        <div className="inline-block animate-marquee text-rose-600 font-bold uppercase tracking-widest">
          I LOVE YOU MORE THAN LEMONS ‚Ä¢ BE MY VALENTINE ‚Ä¢ I'M SORRY I'M LATE ‚Ä¢ YOU'RE THE BEST ‚Ä¢ 
        </div>
      </div>

      {/* MAIN CONTENT CARD */}
      <div className="relative z-10 bg-white p-10 rounded-[3rem] shadow-2xl max-w-sm w-[90%] text-center border-4 border-rose-100">

        {phase === 'loading' && (
          <div>
            <h2 className="text-2xl font-bold text-rose-500 mb-6">Valentine Recovery...</h2>
            <div className="w-full bg-rose-100 h-3 rounded-full mb-4">
              <div 
                className="bg-rose-500 h-full rounded-full transition-all duration-75" 
                style={{ width: `${progress}%` }}
              ></div>
            </div>
            <p className="text-gray-500 animate-pulse text-sm">Deleting my mistakes... {progress}%</p>
          </div>
        )}

        {phase === 'prank' && (
          <div className="animate-in zoom-in duration-500">
            <div className="text-5xl mb-4">üçã</div>
            <h2 className="text-2xl font-bold text-rose-600 mb-2">Wait! One question...</h2>
            <p className="text-gray-600 text-lg mb-8 leading-tight">
              I know I'm late, but will you still be my <br/>
              <span className="font-extrabold text-rose-500">Belated Valentine?</span>
            </p>

            <div className="flex flex-col items-center gap-6 min-h-[140px] justify-center">
              <button 
                onClick={triggerKisses} 
                style={{ transform: `scale(${yesScale})` }}
                className="bg-rose-500 text-white px-8 py-3 rounded-full font-bold shadow-lg hover:bg-rose-600 transition-transform cursor-pointer"
              >
                YES! üíñ
              </button>

              <button 
                onMouseEnter={moveNoButton}
                onClick={moveNoButton}
                style={noStyle}
                className="bg-slate-400 text-white px-6 py-2 rounded-xl text-sm transition-all"
              >
                {noCount > 10 ? "Fine, you win!" : "No, I'm still mad üçã"}
              </button>
            </div>
          </div>
        )}

        {phase === 'success' && (
          <div className="animate-bounce-in">
            <h1 className="text-6xl mb-4">üíã</h1>
            <h2 className="text-3xl font-black text-rose-600 mb-4">YAY!</h2>
            <p className="text-gray-700 font-medium italic">
              "The lemon tree of love has blossomed." <br/>
              I love you so much! ‚ù§Ô∏è
            </p>
            <div className="mt-8 pt-6 border-t border-rose-50 text-[10px] text-gray-400 uppercase tracking-widest">
              Contract signed forever. No refunds.
            </div>
          </div>
        )}
      </div>

      {/* Add this CSS to your global CSS for the marquee effect */}
      <style dangerouslySetInnerHTML={{ __html: `
        @keyframes marquee {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }
        .animate-marquee {
          display: inline-block;
          animation: marquee 15s linear infinite;
        }
      `}} />
    </div>
  );
};

export default App;
