import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);

  // 1. Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 1000);
            return 99;
          }
          return prev + 1;
        });
      }, 40);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. The Runaway "No" Button
  const moveNoButton = () => {
    // Generate random position within 80% of the screen to avoid going off-edge
    const x = Math.random() * (window.innerWidth * 0.7);
    const y = Math.random() * (window.innerHeight * 0.7);

    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      transition: '0.2s ease-out',
      zIndex: 999 
    });

    setNoCount(noCount + 1);
    // Make the Yes button grow slightly
    setYesScale(prev => prev + 0.15);
  };

  // 3. The Kiss Explosion
  const triggerKisses = () => {
    setPhase('success');
    const end = Date.now() + (5 * 1000);

    (function frame() {
      confetti({
        particleCount: 5,
        angle: 60,
        spread: 55,
        origin: { x: 0, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      confetti({
        particleCount: 5,
        angle: 120,
        spread: 55,
        origin: { x: 1, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });

      if (Date.now() < end) {
        requestAnimationFrame(frame);
      }
    }());
  };

  return (
    <div style={styles.container}>
      {/* PHASE: LOADING */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <h2 style={styles.title}>Valentine Recovery Service</h2>
          <div style={styles.progressBarOuter}>
            <div style={{ ...styles.progressBarInner, width: `${progress}%` }} />
          </div>
          <p style={styles.text}>Fetching your belated gift... {progress}%</p>
        </div>
      )}

      {/* PHASE: THE PRANK */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '50px'}}>üçã</div>
          <h2 style={styles.title}>Wait! I have a question...</h2>
          <p style={styles.text}>
            I know I'm late, but will you still be my <br/>
            <strong>Belated Valentine?</strong>
          </p>

          <div style={styles.buttonGroup}>
            <button 
              onClick={triggerKisses} 
              style={{ 
                ...styles.btnYes, 
                transform: `scale(${yesScale})`
              }}
            >
              YES!
            </button>

            <button 
              onMouseEnter={moveNoButton}
              onClick={moveNoButton}
              style={{ ...styles.btnNo, ...noStyle }}
            >
              {noCount > 10 ? "Fine..." : "No"}
            </button>
          </div>

          {noCount > 0 && (
            <p style={styles.errorText}>
              {noCount < 5 ? "Error: Selection not allowed." : "Wow, you're really trying to say no? ü§®"}
            </p>
          )}
        </div>
      )}

      {/* PHASE: SUCCESS */}
      {phase === 'success' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üíã</h1>
          <h2 style={styles.title}>YAY! I Knew it!</h2>
          <p style={styles.text}>Preparing your real surprise... I love you! ‚ù§Ô∏è</p>
          <div style={{marginTop: '20px', fontSize: '12px', color: '#888'}}>
            (P.S. Sorry for the lemon behavior)
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    width: '100vw',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff0f3',
    fontFamily: 'system-ui, -apple-system, sans-serif',
    textAlign: 'center',
    margin: 0,
    overflow: 'hidden'
  },
  card: {
    padding: '40px',
    borderRadius: '25px',
    backgroundColor: 'white',
    boxShadow: '0 15px 35px rgba(0,0,0,0.1)',
    maxWidth: '400px',
    width: '90%',
    zIndex: 10
  },
  title: { color: '#ff4d6d', margin: '10px 0' },
  text: { color: '#444', marginBottom: '20px', fontSize: '1.1rem' },
  progressBarOuter: {
    width: '100%',
    height: '10px',
    backgroundColor: '#eee',
    borderRadius: '10px',
    margin: '20px 0'
  },
  progressBarInner: {
    height: '100%',
    backgroundColor: '#ff4d6d',
    borderRadius: '10px',
    transition: 'width 0.1s linear'
  },
  buttonGroup: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '20px',
    marginTop: '20px',
    position: 'relative',
    height: '100px'
  },
  btnYes: {
    padding: '12px 30px',
    backgroundColor: '#ff4d6d',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'transform 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275)'
  },
  btnNo: {
    padding: '12px 30px',
    backgroundColor: '#6c757d',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  errorText: {
    color: '#ff4d6d',
    fontSize: '0.85rem',
    marginTop: '20px',
    fontWeight: '600'
  }
};

export default App;
