import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [quizStep, setQuizStep] = useState(0);
  const [isNaughty, setIsNaughty] = useState(false);

  // 1. Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 800);
            return 100;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. The "No" Button Shenanigans
  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth - 150);
    const y = Math.random() * (window.innerHeight - 150);
    setNoStyle({ position: 'fixed', left: `${x}px`, top: `${y}px`, zIndex: 999 });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.1);
  };

  // 3. Quiz Questions (The "Prank" Logic)
  const quiz = [
    { q: "Who is the most handsome person in Bengaluru?", a: "You (obviously)", b: "My Boyfriend" },
    { q: "Am I forgiven for being late?", a: "Yes, but it'll cost you", b: "Click 'Yes' to continue" },
    { q: "Choose your 'Dirty' Penalty for me:", a: "Foot massage (1 hour)", b: "A very long, spicy kiss" }
  ];

  const handleQuiz = (choice) => {
    if (quizStep < quiz.length - 1) {
      setQuizStep(quizStep + 1);
    } else {
      triggerSuccess();
    }
  };

  const triggerSuccess = () => {
    setPhase('success');
    confetti({ particleCount: 150, spread: 70, origin: { y: 0.6 }, colors: ['#ff4d6d', '#800020'] });
  };

  return (
    <div style={styles.container}>
      {/* --- LOADING --- */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <div style={styles.emojiSpin}>üç≠</div>
          <h2 style={styles.title}>Locating Forgiveness...</h2>
          <div style={styles.progressBarOuter}><div style={{ ...styles.progressBarInner, width: `${progress}%` }} /></div>
          <p>{progress}% - Retrying connection to her heart...</p>
        </div>
      )}

      {/* --- THE CHASE --- */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '50px'}}>üòà</div>
          <h2 style={styles.title}>Caught You!</h2>
          <p>You can't run from my love (or this late gift).</p>
          <div style={styles.buttonGroup}>
            <button onClick={() => setPhase('quiz')} style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}>YES, I'LL BE YOUR VALENTINE</button>
            <button onMouseEnter={moveNoButton} style={{ ...styles.btnNo, ...noStyle }}>No</button>
          </div>
        </div>
      )}

      {/* --- THE DIRTY/FUN QUIZ --- */}
      {phase === 'quiz' && (
        <div style={styles.card}>
          <span style={{fontSize: '40px'}}>üå∂Ô∏è</span>
          <h2 style={styles.title}>The Forgiveness Test</h2>
          <p style={{fontWeight: 'bold'}}>{quiz[quizStep].q}</p>
          <div style={{display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '20px'}}>
            <button onClick={handleQuiz} style={styles.quizBtn}>{quiz[quizStep].a}</button>
            <button onClick={handleQuiz} style={styles.quizBtn}>{quiz[quizStep].b}</button>
          </div>
        </div>
      )}

      {/* --- FINAL SUCCESS --- */}
      {phase === 'success' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üî•</h1>
          <h2 style={styles.title}>REDEMPTION!</h2>
          <p>I promise to make up for being late. Here is your voucher:</p>

          <div style={styles.naughtyCard} onClick={() => setIsNaughty(!isNaughty)}>
             <p style={{fontSize: '1.2rem', fontWeight: 'bold'}}>
                {isNaughty ? "CLAIMED: One night of absolute mischief üòà" : "TAP TO REVEAL SPICY SURPRISE"}
             </p>
          </div>

          <p style={{fontSize: '0.8rem', marginTop: '20px', color: '#888'}}>
            Location: Krishnaiyyanapalya Headquarters <br/>
            Authorized by: Your favorite person
          </p>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { height: '100vh', width: '100vw', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#1a1a1a', color: '#fff', fontFamily: 'sans-serif', overflow: 'hidden' },
  card: { padding: '40px', borderRadius: '30px', backgroundColor: '#2d2d2d', boxShadow: '0 0 30px #ff4d6d66', maxWidth: '400px', width: '90%', textAlign: 'center', border: '2px solid #ff4d6d' },
  title: { color: '#ff4d6d', marginBottom: '20px' },
  progressBarOuter: { width: '100%', height: '10px', backgroundColor: '#444', borderRadius: '10px', margin: '20px 0' },
  progressBarInner: { height: '100%', backgroundColor: '#ff4d6d', borderRadius: '10px' },
  buttonGroup: { display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', minHeight: '100px' },
  btnYes: { padding: '15px 25px', backgroundColor: '#ff4d6d', color: 'white', border: 'none', borderRadius: '12px', cursor: 'pointer', fontWeight: 'bold' },
  btnNo: { padding: '10px 20px', backgroundColor: '#555', color: 'white', border: 'none', borderRadius: '8px' },
  quizBtn: { padding: '15px', backgroundColor: 'transparent', border: '1px solid #ff4d6d', color: 'white', borderRadius: '10px', cursor: 'pointer', transition: '0.3s', ':hover': { backgroundColor: '#ff4d6d' } },
  naughtyCard: { marginTop: '20px', padding: '20px', backgroundColor: '#ff4d6d', borderRadius: '15px', cursor: 'pointer', transform: 'rotate(-2deg)', boxShadow: '5px 5px 0px #800020' },
  emojiSpin: { fontSize: '50px', animation: 'spin 2s linear infinite' }
};

export default App;
