import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading'); // loading, prank, quiz, success
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [quizStep, setQuizStep] = useState(0);

  // 1. Fake Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 1000);
            return 99;
          }
          return prev + 1;
        });
      }, 40);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. The Runaway "No" Button
  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth * 0.7);
    const y = Math.random() * (window.innerHeight * 0.7);
    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      transition: '0.2s ease-out',
      zIndex: 999 
    });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.15);
  };

  // 3. Quiz Logic
  const handleQuiz = (isCorrect) => {
    if (isCorrect) {
      if (quizStep < 1) {
        setQuizStep(quizStep + 1);
      } else {
        triggerKisses();
      }
    } else {
      alert("WRONG! That's a 5-minute 'No Talking' penalty! Try again. üçã");
    }
  };

  // 4. Final Kisses
  const triggerKisses = () => {
    setPhase('success');
    const end = Date.now() + (5 * 1000);
    (function frame() {
      confetti({
        particleCount: 5,
        angle: 60,
        spread: 55,
        origin: { x: 0, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      confetti({
        particleCount: 5,
        angle: 120,
        spread: 55,
        origin: { x: 1, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      if (Date.now() < end) requestAnimationFrame(frame);
    }());
  };

  return (
    <div style={styles.container}>
      {/* PHASE 1: LOADING */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <h2 style={styles.title}>Valentine Recovery Service</h2>
          <div style={styles.progressBarOuter}>
            <div style={{ ...styles.progressBarInner, width: `${progress}%` }} />
          </div>
          <p style={styles.text}>Scanning for forgiveness... {progress}%</p>
        </div>
      )}

      {/* PHASE 2: THE PRANK (Runaway Button) */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '50px'}}>üçã</div>
          <h2 style={styles.title}>I have a question...</h2>
          <p style={styles.text}>I'm late, but will you still be my <b>Belated Valentine?</b></p>
          <div style={styles.buttonGroup}>
            <button 
              onClick={() => setPhase('quiz')} 
              style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}
            >
              YES!
            </button>
            <button 
              onMouseEnter={moveNoButton}
              onClick={moveNoButton}
              style={{ ...styles.btnNo, ...noStyle }}
            >
              No, you're a lemon
            </button>
          </div>
        </div>
      )}

      {/* PHASE 3: THE FUNNY QUIZ */}
      {phase === 'quiz' && (
        <div style={styles.card}>
          <h2 style={{...styles.title, color: '#ff9f1c'}}>‚ö†Ô∏è Verification Required</h2>
          <p style={styles.text}>Since you said YES, please prove you aren't a robot/still angry:</p>

          {quizStep === 0 ? (
            <div style={styles.quizBox}>
              <p>Who is the prettiest person in the world?</p>
              <button onClick={() => handleQuiz(true)} style={styles.quizBtn}>My Girlfriend (The Boss)</button>
              <button onClick={() => handleQuiz(false)} style={styles.quizBtn}>Megan Fox</button>
              <button onClick={() => handleQuiz(false)} style={styles.quizBtn}>That one girl from TikTok</button>
            </div>
          ) : (
            <div style={styles.quizBox}>
              <p>What is my punishment for being late?</p>
              <button onClick={() => handleQuiz(false)} style={styles.quizBtn}>Break up with me</button>
              <button onClick={() => handleQuiz(true)} style={styles.quizBtn}>Infinity Kisses + Expensive Dinner</button>
            </div>
          )}
        </div>
      )}

      {/* PHASE 4: SUCCESS */}
      {phase === 'success' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üíã</h1>
          <h2 style={styles.title}>YAY! ACCESS GRANTED!</h2>
          <p style={styles.text}>I love you! Now come get your real gift! ‚ù§Ô∏è</p>
          <div style={{marginTop: '20px', fontSize: '10px', color: '#aaa'}}>VERIFIED VALENTINE 2026</div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    width: '100vw',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff0f3',
    fontFamily: 'system-ui, sans-serif',
    textAlign: 'center',
    margin: 0,
    overflow: 'hidden'
  },
  card: {
    padding: '40px',
    borderRadius: '30px',
    backgroundColor: 'white',
    boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
    maxWidth: '400px',
    width: '90%',
    border: '3px solid #ffb3c1'
  },
  title: { color: '#ff4d6d', margin: '10px 0', fontSize: '1.5rem', fontWeight: 'bold' },
  text: { color: '#555', marginBottom: '20px', fontSize: '1rem' },
  progressBarOuter: {
    width: '100%',
    height: '10px',
    backgroundColor: '#eee',
    borderRadius: '10px',
    margin: '20px 0'
  },
  progressBarInner: {
    height: '100%',
    backgroundColor: '#ff4d6d',
    borderRadius: '10px',
    transition: 'width 0.1s'
  },
  buttonGroup: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '15px',
    marginTop: '20px'
  },
  btnYes: {
    padding: '12px 40px',
    backgroundColor: '#ff4d6d',
    color: 'white',
    border: 'none',
    borderRadius: '50px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: '0.2s'
  },
  btnNo: {
    padding: '10px 20px',
    backgroundColor: '#adb5bd',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  quizBox: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    marginTop: '20px'
  },
  quizBtn: {
    padding: '10px',
    backgroundColor: 'white',
    border: '2px solid #ffb3c1',
    borderRadius: '10px',
    cursor: 'pointer',
    color: '#ff4d6d',
    fontWeight: '600'
  }
};

export default App;
