import React, { useState, useEffect, useRef } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [smileScore, setSmileScore] = useState(0);
  const videoRef = useRef(null);

  // 1. Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 800);
            return 100;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. Camera Access for "Smile Scan"
  const startCamera = async () => {
    setPhase('camera-scan');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (err) {
      alert("I need to see that beautiful smile to continue! Please allow camera access. ‚ù§Ô∏è");
      setPhase('prank');
    }
  };

  const handleSmileClick = () => {
    if (smileScore < 100) {
      setSmileScore(prev => prev + 20);
    } else {
      triggerSuccess();
      // Stop the camera stream
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
    }
  };

  // 3. The Runaway Button
  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth - 150);
    const y = Math.random() * (window.innerHeight - 150);
    setNoStyle({ position: 'fixed', left: `${x}px`, top: `${y}px`, zIndex: 999 });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.2);
  };

  const triggerSuccess = () => {
    setPhase('success');
    confetti({ particleCount: 200, spread: 80, origin: { y: 0.6 } });
  };

  return (
    <div style={styles.container}>
      {/* PHASE 1: LOADING */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <div style={styles.spinner}>üíñ</div>
          <h2 style={styles.title}>Booting Apology 2.0</h2>
          <div style={styles.progressBarOuter}><div style={{ ...styles.progressBarInner, width: `${progress}%` }} /></div>
          <p>Analyzing missed Valentine's opportunities... {progress}%</p>
        </div>
      )}

      {/* PHASE 2: THE PRANK */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '60px'}}>üòú</div>
          <h2 style={styles.title}>Wait! Verification Required</h2>
          <p style={styles.text}>I can't just take a "Yes" via button. I need biometric proof of a smile.</p>
          <div style={styles.buttonGroup}>
            <button onClick={startCamera} style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}>OKAY, SCAN ME üì∏</button>
            <button onMouseEnter={moveNoButton} style={{ ...styles.btnNo, ...noStyle }}>I Refuse</button>
          </div>
        </div>
      )}

      {/* PHASE 3: CAMERA SECTION (The "Smile" Prank) */}
      {phase === 'camera-scan' && (
        <div style={styles.card}>
          <h2 style={styles.title}>Biometric Smile Scanner</h2>
          <div style={styles.videoContainer}>
            <video ref={videoRef} autoPlay playsInline style={styles.video} />
            <div style={styles.scannerOverlay} />
          </div>
          <div style={styles.smileBarOuter}>
            <div style={{ ...styles.smileBarInner, width: `${smileScore}%` }} />
          </div>
          <p style={{fontSize: '0.9rem', color: '#ff4d6d'}}>
            {smileScore < 100 ? "SMILE DETECTED: 0.02%... SMILE WIDER!!" : "SMILE VERIFIED! ‚úÖ"}
          </p>
          <button onClick={handleSmileClick} style={styles.captureBtn}>
            {smileScore < 100 ? "TAP TO BOOST SMILE POWER" : "CONTINUE TO REWARD"}
          </button>
        </div>
      )}

      {/* PHASE 4: SUCCESS */}
      {phase === 'success' && (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üíã</h1>
          <h2 style={styles.title}>VERIFIED!</h2>
          <p>That smile is exactly why I'm so lucky. <br/>Gift delivery confirmed for <strong>Bennigana Halli</strong>!</p>
          <div style={styles.naughtySection}>
            <p><strong>Tonight's Penalty for me:</strong></p>
            <p>1. 500 Kisses on arrival <br/> 2. Absolute obedience for 24 hours üòà</p>
          </div>
          <p style={{marginTop: '20px', fontWeight: 'bold'}}>I Love You! ‚ù§Ô∏è</p>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { height: '100vh', width: '100vw', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#ffe5ec', fontFamily: 'sans-serif', overflow: 'hidden' },
  card: { padding: '30px', borderRadius: '30px', backgroundColor: 'white', boxShadow: '0 20px 50px rgba(0,0,0,0.1)', maxWidth: '400px', width: '90%', textAlign: 'center' },
  title: { color: '#ff4d6d' },
  text: { fontSize: '1rem', color: '#444' },
  videoContainer: { position: 'relative', width: '100%', borderRadius: '20px', overflow: 'hidden', margin: '15px 0', border: '3px solid #ff4d6d' },
  video: { width: '100%', height: 'auto', transform: 'scaleX(-1)' },
  scannerOverlay: { position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: 'linear-gradient(rgba(255,77,109,0.1) 50%, rgba(255,77,109,0.4) 50%)', backgroundSize: '100% 4px', animation: 'scan 2s linear infinite' },
  smileBarOuter: { width: '100%', height: '20px', backgroundColor: '#eee', borderRadius: '10px', overflow: 'hidden', margin: '10px 0' },
  smileBarInner: { height: '100%', backgroundColor: '#ff4d6d', transition: 'width 0.3s ease' },
  captureBtn: { padding: '12px 20px', backgroundColor: '#ff4d6d', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold' },
  btnYes: { padding: '15px 25px', backgroundColor: '#ff4d6d', color: 'white', border: 'none', borderRadius: '12px', cursor: 'pointer', fontWeight: 'bold' },
  btnNo: { padding: '10px 20px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '8px' },
  naughtySection: { backgroundColor: '#fff0f3', padding: '15px', borderRadius: '15px', marginTop: '15px', border: '1px dashed #ff4d6d' },
  spinner: { fontSize: '40px', animation: 'spin 2s linear infinite' },
  progressBarOuter: { width: '100%', height: '10px', backgroundColor: '#eee', borderRadius: '10px', margin: '15px 0' },
  progressBarInner: { height: '100%', backgroundColor: '#ff4d6d', borderRadius: '10px' }
};

export default App;
