import React, { useState, useEffect } from 'react';
import ErrorModal from './components/ErrorModal';
import ApologySection from './components/ApologySection';
import QuizSection from './components/QuizSection';
import RomanticCard from './components/RomanticCard';
import KissExplosion from './components/KissExplosion';
import './styles/App.css';

function App() {
  const [currentSection, setCurrentSection] = useState('error'); // 'error', 'apology', 'quiz', 'romantic', 'explosion'

  return (
    <div className="App">
      {currentSection === 'error' && (
        <ErrorModal onComplete={() => setCurrentSection('apology')} />
      )}

      {currentSection === 'apology' && (
        <ApologySection onNext={() => setCurrentSection('quiz')} />
      )}

      {currentSection === 'quiz' && (
        <QuizSection onNext={() => setCurrentSection('romantic')} />
      )}

      {currentSection === 'romantic' && (
        <RomanticCard onNext={() => setCurrentSection('explosion')} />
      )}

      {currentSection === 'explosion' && (
        <KissExplosion />
      )}
    </div>
  );
}

export default App;
