import React, { useState } from 'react';
import '../styles/QuizSection.css';

const QuizSection = ({ onNext }) => {
  const [sliderValue, setSliderValue] = useState(50);
  const [feedback, setFeedback] = useState('Not enough, try harder!');

  const handleSliderChange = (e) => {
    const value = parseInt(e.target.value);
    setSliderValue(value);

    if (value === 100) {
      setFeedback('Perfect! Now you get the real surprise!');
      // Move to next section after a delay
      setTimeout(() => {
        onNext();
      }, 1500);
    } else {
      setFeedback('Not enough, try harder!');
    }
  };

  return (
    <div className="quiz-section section">
      <h2>How much do you love me?</h2>
      <input
        type="range"
        id="loveSlider"
        min="0"
        max="100"
        value={sliderValue}
        onChange={handleSliderChange}
        className="love-slider"
      />
      <p id="sliderFeedback" className="slider-feedback">
        {feedback}
      </p>
    </div>
  );
};

export default QuizSection;
