import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [messageIndex, setMessageIndex] = useState(0);

  // New Fun States
  const [penalty, setPenalty] = useState(null);
  const [isSpinning, setIsSpinning] = useState(false);

  const phrases = [
    "No", "Are you sure?", "Really sure??", "Think again!", "Last chance!",
    "Surely not?", "You're breaking my heart! üíî", "I'll cry...", "I'm crying now üò≠",
    "Okay, that's enough.", "PLEASEEE", "I'll give you chocolate! üç´", "Look behind you! (jk)",
    "Just click Yes already!", "You're mean! ü•∫", "I give up...", "Error 404: No not found"
  ];

  const penalties = [
    "100 Forehead Kisses üíã",
    "Buy her a dessert of her choice üç∞",
    "Full body massage (No time limit) üíÜ‚Äç‚ôÄÔ∏è",
    "A 10-minute 'I am sorry' speech üé§",
    "Cook her favorite meal tonight ü•ò",
    "Wear a 'Late Valentine' hat all day üß¢",
    "Transfer 500 'Love Credits' (Kisses) üèß"
  ];

  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 800);
            return 100;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth - 150);
    const y = Math.random() * (window.innerHeight - 150);
    setNoStyle({ 
      position: 'fixed', left: `${x}px', top: '${y}px`, 
      transition: '0.15s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
      zIndex: 999,
      fontSize: `${Math.max(16 - noCount, 4)}px` 
    });
    setNoCount(noCount + 1);
    setMessageIndex(noCount % phrases.length);
    setYesScale(prev => prev + 0.2);
  };

  const triggerSuccess = () => {
    setPhase('success');
    const end = Date.now() + (5 * 1000);
    (function frame() {
      confetti({ particleCount: 3, angle: 60, spread: 55, origin: { x: 0 }, colors: ['#ff4d6d', '#ff0000'] });
      confetti({ particleCount: 3, angle: 120, spread: 55, origin: { x: 1 }, colors: ['#ff4d6d', '#ff0000'] });
      if (Date.now() < end) requestAnimationFrame(frame);
    }());
  };

  const spinPenalty = () => {
    setIsSpinning(true);
    setPenalty(null);
    setTimeout(() => {
      const randomPenalty = penalties[Math.floor(Math.random() * penalties.length)];
      setPenalty(randomPenalty);
      setIsSpinning(false);
      confetti({ particleCount: 50, spread: 60, origin: { y: 0.8 } });
    }, 2000);
  };

  return (
    <div style={styles.container}>
      {/* PHASE 1: LOADING */}
      {phase === 'loading' && (
        <div style={styles.card}>
          <div style={styles.spinner}>üíñ</div>
          <h2 style={styles.title}>System Apology Update</h2>
          <div style={styles.progressBarOuter}><div style={{ ...styles.progressBarInner, width: `${progress}%` }} /></div>
          <p style={styles.text}>Scanning for forgiveness levels... {progress}%</p>
        </div>
      )}

      {/* PHASE 2: PRANK */}
      {phase === 'prank' && (
        <div style={styles.card}>
          <div style={{fontSize: '60px'}}>üíù</div>
          <h2 style={styles.title}>I know I'm a bit late...</h2>
          <p style={styles.text}>But my heart is still running on Feb 14th time.<br/><strong>Be my Belated Valentine?</strong></p>
          <div style={styles.buttonGroup}>
            <button onClick={triggerSuccess} style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}>YES! üåπ</button>
            <button onMouseEnter={moveNoButton} onClick={moveNoButton} style={{ ...styles.btnNo, ...noStyle }}>{phrases[messageIndex]}</button>
          </div>
        </div>
      )}

      {/* PHASE 3: SUCCESS & THE PENALTY WHEEL */}
      {phase === 'success' && (
        <div style={{...styles.card, animation: 'popIn 0.5s ease-out'}}>
          <h1 style={{fontSize: '4rem', margin: '0'}}>üè†</h1>
          <h2 style={styles.title}>VICTORY! ‚ù§Ô∏è</h2>
          <p style={styles.text}>Gift Delivery Inbound to <strong>Bennigana Halli!</strong></p>

          <hr style={{border: '0.5px solid #eee', margin: '20px 0'}} />

          <h3 style={{color: '#ff4d6d'}}>Step 2: Choose My Penalty üòà</h3>
          <p style={{fontSize: '0.9rem'}}>Since I was late, I must be punished. Spin to see what I owe you!</p>

          <div style={styles.wheelContainer}>
            <div style={{...styles.wheel, animation: isSpinning ? 'spin 0.5s linear infinite' : 'none'}}>
              {isSpinning ? "üåÄ" : "üé°"}
            </div>
            {penalty && (
              <div style={styles.penaltyResult}>
                <strong>YOU WON:</strong> <br/> {penalty}
              </div>
            )}
          </div>

          <button 
            onClick={spinPenalty} 
            disabled={isSpinning}
            style={styles.btnSpin}
          >
            {isSpinning ? "SPINNING..." : "SPIN FOR PENALTY"}
          </button>

          <div style={styles.contract}>
            <p style={{fontSize: '10px', color: '#888'}}>* By spinning, you agree that Vishnu is the best boyfriend ever.</p>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { height: '100vh', width: '100vw', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#ffe5ec', fontFamily: 'cursive', textAlign: 'center', overflowX: 'hidden' },
  card: { padding: '30px', borderRadius: '30px', backgroundColor: 'white', boxShadow: '0 20px 50px rgba(255, 77, 109, 0.3)', maxWidth: '400px', width: '90%' },
  title: { color: '#ff4d6d', fontSize: '1.5rem' },
  text: { color: '#555', fontSize: '1rem' },
  progressBarOuter: { height: '10px', backgroundColor: '#ffd1dc', borderRadius: '10px', margin: '15px 0' },
  progressBarInner: { height: '100%', backgroundColor: '#ff4d6d', borderRadius: '10px', transition: 'width 0.1s linear' },
  buttonGroup: { display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', minHeight: '120px' },
  btnYes: { padding: '12px 25px', backgroundColor: '#ff4d6d', color: 'white', border: 'none', borderRadius: '50px', cursor: 'pointer', fontWeight: 'bold' },
  btnNo: { padding: '10px 20px', backgroundColor: '#adb5bd', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer' },
  btnSpin: { marginTop: '20px', padding: '12px 20px', backgroundColor: '#ff4d6d', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold', width: '100%' },
  wheelContainer: { margin: '20px 0', minHeight: '100px', display: 'flex', flexDirection: 'column', alignItems: 'center' },
  wheel: { fontSize: '50px', transition: 'transform 2s ease-out' },
  penaltyResult: { marginTop: '15px', padding: '15px', backgroundColor: '#fff0f3', borderRadius: '15px', border: '2px dashed #ff4d6d', color: '#ff4d6d', animation: 'popIn 0.3s ease-out' },
  contract: { marginTop: '20px' },
  spinner: { fontSize: '40px', display: 'inline-block', animation: 'spin 2s linear infinite' }
};

// Add this to your Global CSS or a <style> tag
const animations = `
@keyframes spin { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }
@keyframes popIn { 0% { transform: scale(0.5); opacity: 0; } 100% { transform: scale(1); opacity: 1; } }
@keyframes float { 0% { transform: translateY(0px); } 50% { transform: translateY(-10px); } 100% { transform: translateY(0px); } }
`;

export default App;
