import React, { useState, useRef, useEffect } from 'react';
import '../styles/ApologySection.css';

const ApologySection = ({ onNext }) => {
  const [yesPosition, setYesPosition] = useState({ x: 0, y: 0 });
  const [noPosition, setNoPosition] = useState({ x: 0, y: 0 });
  const yesButtonRef = useRef(null);
  const noButtonRef = useRef(null);

  // Initialize button positions
  useEffect(() => {
    positionButtonRandomly('yes');
    positionButtonRandomly('no');
  }, []);

  const positionButtonRandomly = (buttonType) => {
    const maxX = window.innerWidth - 100; // Approximate button width
    const maxY = window.innerHeight - 50; // Approximate button height

    const randomX = Math.floor(Math.random() * maxX);
    const randomY = Math.floor(Math.random() * maxY);

    if (buttonType === 'yes') {
      setYesPosition({ x: randomX, y: randomY });
    } else {
      setNoPosition({ x: randomX, y: randomY });
    }
  };

  const handleYesHover = (e) => {
    e.preventDefault();
    positionButtonRandomly('yes');
  };

  const handleYesClick = () => {
    // Advance to next section when Yes is clicked
    onNext();
  };

  const handleNoHover = (e) => {
    e.preventDefault();
    positionButtonRandomly('no');
  };

  const handleNoClick = (e) => {
    e.preventDefault();
    positionButtonRandomly('no');
  };

  return (
    <div className="apology-section section">
      <h1 id="apologyText">I know I missed the day... will you forgive me?</h1>
      <div className="button-container">
        <button
          id="yesButton"
          className="choice-button"
          style={{
            position: 'absolute',
            left: `${yesPosition.x}px`,
            top: `${yesPosition.y}px`,
          }}
          onMouseEnter={handleYesHover}
          onClick={handleYesClick}
          ref={yesButtonRef}
        >
          Yes
        </button>
        <button
          id="noButton"
          className="choice-button"
          style={{
            position: 'absolute',
            left: `${noPosition.x}px`,
            top: `${noPosition.y}px`,
          }}
          onMouseEnter={handleNoHover}
          onClick={handleNoClick}
          ref={noButtonRef}
        >
          No
        </button>
      </div>
    </div>
  );
};

export default ApologySection;
