import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading'); // loading, prank, quiz, success
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);
  const [quizStep, setQuizStep] = useState(0);

  // 1. Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 1000);
            return 99;
          }
          return prev + 1;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  // 2. The Runaway "No" Button
  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth * 0.7);
    const y = Math.random() * (window.innerHeight * 0.7);
    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      zIndex: 999 
    });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.2);
  };

  // 3. Quiz Logic
  const handleQuiz = (isCorrect) => {
    if (isCorrect) {
      if (quizStep < 2) {
        setQuizStep(quizStep + 1);
      } else {
        triggerKisses();
      }
    } else {
      alert("WRONG! Try again, my grumpy lemon! üçã");
    }
  };

  // 4. The Finale
  const triggerKisses = () => {
    setPhase('success');
    const end = Date.now() + (5 * 1000);
    const frame = () => {
      confetti({
        particleCount: 5,
        angle: 60,
        spread: 55,
        origin: { x: 0, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      confetti({
        particleCount: 5,
        angle: 120,
        spread: 55,
        origin: { x: 1, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1']
      });
      if (Date.now() < end) requestAnimationFrame(frame);
    };
    frame();
  };

  return (
    <div className="h-screen w-screen bg-rose-50 flex items-center justify-center font-sans overflow-hidden p-4">

      {/* PHASE: LOADING */}
      {phase === 'loading' && (
        <div className="bg-white p-8 rounded-3xl shadow-xl w-full max-w-sm text-center border-4 border-rose-100">
          <h2 className="text-rose-500 font-bold text-xl mb-4 text-pretty">Valentine Recovery Service</h2>
          <div className="w-full bg-gray-200 h-3 rounded-full overflow-hidden mb-2">
            <div className="bg-rose-500 h-full transition-all duration-100" style={{ width: `${progress}%` }}></div>
          </div>
          <p className="text-gray-400 text-sm">Deleting my mistakes... {progress}%</p>
        </div>
      )}

      {/* PHASE: THE PRANK */}
      {phase === 'prank' && (
        <div className="bg-white p-10 rounded-[3rem] shadow-2xl w-full max-w-sm text-center animate-in zoom-in duration-300">
          <div className="text-5xl mb-4">üçã</div>
          <h2 className="text-2xl font-bold text-rose-600 mb-2">Wait! One question...</h2>
          <p className="text-gray-600 mb-8 leading-tight">
            I know I'm late, but will you still be my <br/>
            <span className="font-extrabold text-rose-500 underline">Belated Valentine?</span>
          </p>

          <div className="flex flex-col items-center gap-4 min-h-[150px] justify-center">
            <button 
              onClick={() => setPhase('quiz')} 
              style={{ transform: `scale(${yesScale})` }}
              className="bg-rose-500 text-white px-10 py-3 rounded-full font-bold shadow-lg transition-transform active:scale-95"
            >
              YES! üíñ
            </button>

            <button 
              onMouseEnter={moveNoButton}
              onClick={moveNoButton}
              style={noStyle}
              className="bg-gray-400 text-white px-6 py-2 rounded-xl text-sm transition-all duration-200"
            >
              No, I'm still mad
            </button>
          </div>
        </div>
      )}

      {/* PHASE: THE FUNNY QUIZ */}
      {phase === 'quiz' && (
        <div className="bg-white p-10 rounded-[3rem] shadow-2xl w-full max-w-md text-center border-t-8 border-rose-400">
          <h2 className="text-xl font-black text-rose-600 mb-6 uppercase tracking-widest">‚ö†Ô∏è Security Check</h2>

          {quizStep === 0 && (
            <div className="animate-in slide-in-from-right duration-300">
              <p className="text-lg text-gray-700 mb-6">How angry are you right now?</p>
              <div className="grid grid-cols-1 gap-3">
                <button onClick={() => handleQuiz(false)} className="border-2 border-rose-200 p-3 rounded-xl hover:bg-rose-50 transition">I want to bite you ü¶∑</button>
                <button onClick={() => handleQuiz(true)} className="border-2 border-rose-200 p-3 rounded-xl hover:bg-rose-50 transition">I forgive you (barely) üôÑ</button>
                <button onClick={() => handleQuiz(false)} className="border-2 border-rose-200 p-3 rounded-xl hover:bg-rose-50 transition">I'm selling your PlayStation üéÆ</button>
              </div>
            </div>
          )}

          {quizStep === 1 && (
            <div className="animate-in slide-in-from-right duration-300">
              <p className="text-lg text-gray-700 mb-6">Who is the best at lemon agriculture?</p>
              <div className="grid grid-cols-1 gap-3">
                <button onClick={() => handleQuiz(true)} className="border-2 border-rose-200 p-3 rounded-xl hover:bg-rose-50 transition">Me, obviously üëë</button>
                <button onClick={() => handleQuiz(false)} className="border-2 border-rose-200 p-3 rounded-xl hover:bg-rose-50 transition">You (The late guy) ü§°</button>
              </div>
            </div>
          )}

          {quizStep === 2 && (
            <div className="animate-in slide-in-from-right duration-300">
              <p className="text-lg text-gray-700 mb-6">Final Step: Do you love me?</p>
              <div className="grid grid-cols-2 gap-3">
                <button onClick={() => handleQuiz(true)} className="bg-green-500 text-white p-4 rounded-xl font-bold">YES</button>
                <button onClick={() => handleQuiz(true)} className="bg-rose-500 text-white p-4 rounded-xl font-bold">ABSOLUTELY</button>
              </div>
            </div>
          )}
        </div>
      )}

      {/* PHASE: SUCCESS */}
      {phase === 'success' && (
        <div className="bg-white p-12 rounded-[4rem] shadow-2xl w-full max-w-sm text-center border-b-8 border-rose-500 animate-bounce-in">
          <h1 className="text-7xl mb-6">üíã</h1>
          <h2 className="text-3xl font-black text-rose-600 mb-2">MWAH!</h2>
          <p className="text-gray-500 font-medium italic">
            Valentine Verification Complete. <br/>
            I love you more than all the lemons in the world! ‚ù§Ô∏è
          </p>
          <div className="mt-8 text-[10px] text-gray-300 tracking-[0.2em] uppercase font-bold">
            Certified Late but Lovely
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
