    "Just click Yes already!", "You're mean! ü•∫", "Error 404: No not found"
  ];

  // 1. Loading Logic
  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setTimeout(() => setPhase('prank'), 800);
