        "> Initializing Apology_V3.exe...",
        "> Scanning mood database...",
        "> WARNING: Forgiveness levels low.",
        "> Overriding anger protocols...",
        "> Accessing 'Bennigana Halli' GPS...",
        "> Locating Sourav's favorite person...",
        "> Found! Preparing premium rewards...",
        "> SUCCESS: Portal to Chaos opened."
      ];
      let i = 0;
      const hackInterval = setInterval(() => {
        if (i < lines.length) {
          setHackerText(prev => prev + lines[i] + "\n");
          i++;
        } else {
          clearInterval(hackInterval);
          setTimeout(() => setPhase('naughty-coupon'), 1500);
        }
      }, 800);
      return () => clearInterval(hackInterval);
    }
  }, [phase]);

  // 2. Camera Trigger
  const startCamera = async () => {
    setPhase('camera-scan');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) videoRef.current.srcObject = stream;
    } catch (err) {
      alert("I need to see that smile to continue! ‚ù§Ô∏è");
      setPhase('prank');
