    setPhase('success');
    confetti({ particleCount: 150, spread: 70, origin: { y: 0.6 } });
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div style={styles.container}>
