      {/* PHASE 5: HACKER OVERLOAD */}
      {phase === 'hacker' && (
        <div style={{...styles.card, backgroundColor: '#000', border: '2px solid #0f0'}}>
          <h2 style={{color: '#0f0', fontFamily: 'monospace'}}>OVERRIDING SYSTEM...</h2>
