import React, { useState, useEffect } from 'react';
import confetti from 'canvas-confetti';

const App = () => {
  const [phase, setPhase] = useState('loading');
  const [progress, setProgress] = useState(0);
  const [noCount, setNoCount] = useState(0);
  const [noStyle, setNoStyle] = useState({});
  const [yesScale, setYesScale] = useState(1);

  useEffect(() => {
    if (phase === 'loading') {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 99) {
            clearInterval(interval);
            setTimeout(() => {
              setPhase('prank');
              setShowSidebar(true); // Pop the sidebar once loaded
            }, 800);
            return 99;
          }
          return prev + 2;
        });
      }, 30);
      return () => clearInterval(interval);
    }
  }, [phase]);

  const moveNoButton = () => {
    const x = Math.random() * (window.innerWidth * 0.7);
    const y = Math.random() * (window.innerHeight * 0.7);
    setNoStyle({ 
      position: 'fixed', 
      left: `${x}px`, 
      top: `${y}px`, 
      transition: '0.15s cubic-bezier(0.18, 0.89, 0.32, 1.28)',
      zIndex: 999 
    });
    setNoCount(noCount + 1);
    setYesScale(prev => prev + 0.2); // Yes button gets huge!
  };

  const triggerKisses = () => {
    setPhase('success');
    const end = Date.now() + (6 * 1000);

    const frame = () => {
      confetti({
        particleCount: 7,
        angle: 60,
        spread: 55,
        origin: { x: 0, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1'],
        shapes: ['circle']
      });
      confetti({
        particleCount: 7,
        angle: 120,
        spread: 55,
        origin: { x: 1, y: 0.8 },
        colors: ['#ff4d6d', '#ff0000', '#ff85a1'],
        shapes: ['circle']
      });

      if (Date.now() < end) requestAnimationFrame(frame);
    };
    frame();
  };

  return (
    <div style={styles.container}>
      {/* SIDEBAR POP-IN */}
      <div style={{ ...styles.sidebar, left: showSidebar ? '0' : '-300px' }}>
        <h3 style={{color: '#fff'}}>Top 3 Reasons I'm Late:</h3>
        <ul style={styles.list}>
          <li>üçã Fighting off giant lemons</li>
          <li>üó∫Ô∏è Got lost in a dream of you</li>
          <li>üê¢ Riding a slow turtle to your house</li>
        </ul>
        <div style={styles.miniCard}>
          <p><strong>Forgiveness Status:</strong> {noCount > 5 ? "ALMOST THERE" : "PENDING..."}</p>
        </div>
      </div>

      {/* MAIN CARD */}
      {phase === 'loading' ? (
        <div style={styles.card}>
          <h2 style={styles.title}>Booting "Super Boyfriend" Mode...</h2>
          <div style={styles.progressBarOuter}>
            <div style={{ ...styles.progressBarInner, width: `${progress}%` }} />
          </div>
          <p>{progress}%</p>
        </div>
      ) : phase === 'prank' ? (
        <div style={styles.card}>
          <span style={{fontSize: '50px'}}>üíù</span>
          <h2 style={styles.title}>Will You Be My Belated Valentine?</h2>
          <p style={styles.text}>I'm sorry I missed the day, but I won't miss the chance to love you!</p>

          <div style={styles.buttonGroup}>
            <button 
              onClick={triggerKisses} 
              style={{ ...styles.btnYes, transform: `scale(${yesScale})` }}
            >
              YES! üíñ
            </button>
            <button 
              onMouseEnter={moveNoButton}
              style={{ ...styles.btnNo, ...noStyle }}
            >
              No üçã
            </button>
          </div>
        </div>
      ) : (
        <div style={styles.card}>
          <h1 style={{fontSize: '4rem'}}>üíã</h1>
          <h2 style={styles.title}>SUCCESS!</h2>
          <p style={styles.text}>The contract is signed! You are my Valentine forever. ‚ù§Ô∏è</p>
          <div style={styles.statsBox}>
            <p><strong>Kisses Owed:</strong> 1,000,000+</p>
            <p><strong>Lemon Farming Help:</strong> Mandatory</p>
          </div>
        </div>
      )}

      {/* BOTTOM TICKER */}
      <div style={styles.ticker}>
        I LOVE YOU MORE THAN LEMONS ‚Ä¢ PLEASE SAY YES ‚Ä¢ SORRY I'M LATE ‚Ä¢ YOU ARE BEAUTIFUL ‚Ä¢ 
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    width: '100vw',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff0f3',
    fontFamily: '"Comic Sans MS", "Chalkboard SE", sans-serif', // Playful font
    margin: 0,
    overflow: 'hidden',
    position: 'relative'
  },
  sidebar: {
    position: 'fixed',
    top: 0,
    width: '260px',
    height: '100%',
    backgroundColor: '#ff4d6d',
    padding: '40px 20px',
    boxShadow: '5px 0 15px rgba(0,0,0,0.1)',
    transition: '0.8s cubic-bezier(0.68, -0.55, 0.27, 1.55)',
    zIndex: 100,
    color: 'white',
    textAlign: 'left'
  },
  card: {
    padding: '40px',
    borderRadius: '30px',
    backgroundColor: 'white',
    boxShadow: '0 20px 50px rgba(255, 77, 109, 0.3)',
    maxWidth: '420px',
    width: '90%',
    zIndex: 10,
    border: '4px solid #ffb3c1'
  },
  title: { color: '#ff4d6d', fontSize: '1.8rem' },
  text: { color: '#666', fontSize: '1.1rem' },
  progressBarOuter: {
    width: '100%',
    height: '15px',
    backgroundColor: '#ffe5ec',
    borderRadius: '20px',
    margin: '20px 0'
  },
  progressBarInner: {
    height: '100%',
    backgroundColor: '#ff4d6d',
    borderRadius: '20px',
    transition: 'width 0.1s linear'
  },
  buttonGroup: {
    marginTop: '40px',
    height: '120px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  btnYes: {
    padding: '15px 40px',
    backgroundColor: '#ff4d6d',
    color: 'white',
    border: 'none',
    borderRadius: '50px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '1.2rem',
    boxShadow: '0 8px 20px rgba(255, 77, 109, 0.4)',
    zIndex: 20
  },
  btnNo: {
    padding: '10px 25px',
    backgroundColor: '#adb5bd',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer'
  },
  list: { paddingLeft: '20px', lineHeight: '2' },
  miniCard: {
    background: 'rgba(255,255,255,0.2)',
    padding: '10px',
    borderRadius: '10px',
    marginTop: '20px'
  },
  statsBox: {
    background: '#fff0f3',
    padding: '15px',
    borderRadius: '15px',
    marginTop: '20px',
    textAlign: 'left'
  },
  ticker: {
    position: 'fixed',
    bottom: 0,
    width: '100%',
    backgroundColor: '#ffb3c1',
    color: '#ff4d6d',
    padding: '10px 0',
    fontWeight: 'bold',
    fontSize: '0.9rem',
    whiteSpace: 'nowrap'
  }
};

export default App;
